模式​ ​含义​ os.O_WRONLY ​只​写​ os.O_CREATE ​创建​文件​ os.O_RDONLY ​只读​ os.O_RDWR ​读写​ os.O_TRUNC ​清空​ os.O_APPEND ​追加​ <pre><code>package main

import (
    "bufio"
    "fmt"
    "io"
    "os"
)

func w() {
    f, err := os.OpenFile("./m.txt", os.O_CREATE|os.O_WRONLY, 0777)
    if err != nil {
        return
    }
    defer f.Close()

    writer := bufio.NewWriter(f)
    for i := 0; i &lt; 100; i++ {
        writer.WriteString("matt\n")
    }

    writer.Flush()

}

func r() {
    f, err := os.Open("./m.txt")
    if err != nil {
        fmt.Println(err)
        return
    }
    defer f.Close()
    reader := bufio.NewReaderSize(f, 100)
    for {
        line, prefix, err := reader.ReadLine()
        if err == io.EOF {
            break
        }
        if err != nil {
            return
        }
        fmt.Println(string(line), prefix)
    }
}

func main() {
    //w()
    r()
}
</code></pre>
